<span class="token punctuation">}</span>

<span class="token keyword">const</span> str <span class="token operator">=</span> <span class="token function">lcs</span><span class="token punctuation">(</span><span class="token string">"adbcsda"</span><span class="token punctuation">,</span> <span class="token string">"bcsadb"</span><span class="token punctuation">)</span><span class="token punctuation">;</span>
